CREATE TABLE `PlayerPrivacy` ( `PlayerId` INT(11) UNSIGNED NOT NULL , `Name` TINYINT NOT NULL DEFAULT '0' , `Surname` TINYINT NOT NULL DEFAULT '0' , `Gender` TINYINT NOT NULL DEFAULT '2' , `Birthday` TINYINT NOT NULL DEFAULT '0' , `Age` TINYINT NOT NULL DEFAULT '0' , `Zip` TINYINT NOT NULL DEFAULT '0' , `Address` TINYINT NOT NULL DEFAULT '0' , `City` TINYINT NOT NULL DEFAULT '2' , `Country` TINYINT NOT NULL DEFAULT '2' , PRIMARY KEY (`PlayerId`) ) ENGINE = InnoDB;
INSERT INTO `PlayerPrivacy` (`PlayerId`) SELECT `Id` FROM `Players`;