CREATE TABLE `PlayerTmpCaptcha` ( `PlayerId` INT(11) UNSIGNED NOT NULL , `Date` INT(11) UNSIGNED NOT NULL , PRIMARY KEY (`PlayerId`) ) ENGINE = MEMORY;
CREATE TABLE `PlayerCaptcha` ( `PlayerId` INT(11) UNSIGNED NOT NULL , `Day` INT(11) UNSIGNED NOT NULL , `Date` INT(11) UNSIGNED NOT NULL , `Time` INT(11) UNSIGNED NOT NULL ) ENGINE = InnoDB;
CREATE TABLE `PlayerCounters` ( `PlayerId` INT(11) UNSIGNED NOT NULL , `CaptchaCount` INT(11) UNSIGNED NOT NULL DEFAULT '0' , `CaptchaTime` INT(11) UNSIGNED NOT NULL DEFAULT '0' ) ENGINE = InnoDB;
INSERT INTO `PlayerCounters` ( `PlayerId` ) SELECT `Id` FROM `Players`;